mapper.select:
select count(*) from likes
	where board_num = #{boardNum} 
				and user_num = #{userNum} 
				and board_type = ${boardType}

mapper.delete:
		delete from likes
  			where user_num = #{userNum}
  			and board_num = #{boardNum}
  			and board_type = ${boardType}

mapper.select:
select count(*) from likes
 where board_num = #{boardNum}
 	and board_type = #{boardType}
