mapper.update:
UPDATE CHATMESSAGE 
SET IS_READ = 'Y'
WHERE CHATROOM_ID = #{chatroomId}
AND RECEIVER_NUM = #{userNum}

mapper.select:
SELECT * FROM CHATROOM 
	LEFT JOIN MEMBER ON (CHATROOM.USER_NUM = MEMBER.USER_NUM)
WHERE CHATROOM_ID =
	(
	SELECT CHATROOM_ID FROM CHATMESSAGE WHERE CHAT_ID = #{chatId}
	)
