mapper.update:
update review
set rv_Content = #{rvContent}, rv_star = #{rvStar}
where pro_num = #{proNum} and user_num = #{userNum}

update review
set rv_status = #{rvStatus}
where pro_num = #{proNum} and user_num = #{userNum}

mapper.select:
select ATTACH_RENAME
from attachment
where BOARD_TYPE= 7 and SERIALNUMBER = #{rvNum}

select *
from member
where user_status='Y' and user_num=#{i}
